var iitm_app = iitm_app || {};
iitm_app = {
  init: function () {
    iitm_app.triggerNoImage();

    jQuery(window).on("load ajaxComplete", function (event, xhr, settings) {
      if (
        jQuery("[data-popup-overlay], [data-popup-model]").hasClass("active")
      ) {
        jQuery("[data-popup-close]").on("click", function () {
          var key = jQuery("#keyword").val("");
          jQuery("#popup_content").text("");
          jQuery("#popup_location").text("");
          jQuery("#popup_people").text("");
          jQuery("#wrapper").html("");
          jQuery(".findinfo__controls").slideUp();
          jQuery(".findinfo__label, .findinfo__searchbox").removeClass(
            "hidelabel"
          );
          jQuery(".findinfo__morewaystext a").show();
          jQuery("[data-popup-overlay], [data-popup-model]").removeClass(
            "active"
          );
          jQuery("input:checkbox[class=popup_search_type]").each(function () {
            jQuery(".popup_search_type").prop("checked", false);
          });
          jQuery("[aria-expanded]").attr("aria-expanded", false);
          jQuery("body").removeAttr("style");
        });
      }

      //added to remove feedback form top summary error messages
      jQuery(".header__commentsForm .error-msg").addClass("hide");

      if (
        jQuery(".hidden_captcha div").hasClass("form-item--error-message") &&
        grecaptcha != undefined
      ) {
        grecaptcha.render("g-recaptcha", {
          sitekey: jQuery("#g-recaptcha").data("sitekey"),
        });
      }
    });

    //added to remove width/height attributes for all basic pages
    jQuery(".content__page img").removeAttr("width").removeAttr("height");

    //added to remove no result in rs page
    jQuery(".rs_no_results").html("");

    if (jQuery(".rs_dropdown").length > 0) {
      iitm_app.getResearchAreasByDept();
    }
  },
  triggerNoImage: function () {
    jQuery(window).on("load ajaxComplete", function (event, xhr, settings) {
      iitm_app.setNoImage();
      iitm_app.highlightSearchText();
    });
  },
  setNoImage: function () {
    if (jQuery("[data-noimg]").length > 0) {
      var noimg_count = 1;
      var arr = [
        {
          press: 5,
          seminar: 10,
          eventssports: 5,
          people: 10,
          research: 1,
        },
      ];
      var arr_data = arr[0];
      jQuery("[data-noimg]").each(function () {
        var category = jQuery(this).data("noimg-category");
        var type_name = category.split("_")[0];

        noimg_count = arr_data[type_name];
        if (noimg_count) {
          no_image =
            "/themes/custom/iitm/assets/images/no_img/" +
            category +
            "/" +
            Math.floor(Math.random() * noimg_count + 1) +
            ".png";
          if (category == "people_s4") {
            jQuery(this).css("background-image", "url(" + no_image + ")");
          } else {
            jQuery(this).attr("src", no_image);
          }
        }
      });
    }
  },
  highlightSearchText: function () {
    var searchword = jQuery("input[name=field_rs_research_areas_value]").val();

    if (searchword != undefined && searchword.length > 0) {
      jQuery(".person__list").mark(searchword);
    }
  },
  getResearchAreasByDept: function () {
    jQuery(".rs_btn_show").on("click", function (e) {
      e.preventDefault();
      iitm_app.updateQueryParam();
      var dept = jQuery(".rs_dropdown").val();
      var err = "";

      var base_url =
        drupalSettings.language == "hi"
          ? window.location.origin + "/" + drupalSettings.language
          : window.location.origin;

      var qparam = iitm_app.getQueryParams();

      if (
        qparam &&
        qparam.department != undefined &&
        qparam.page != undefined
      ) {
        var post_data =
          "page=" + qparam.page + "&department=" + qparam.department;
      }

      //jQuery('.view-display-id-mar_by_department').html("");

      jQuery.ajax({
        type: "GET",
        url: base_url + "/custom/ajax/mar_department",
        data: "department=" + dept,
        dataType: "html",
        async: true,
        complete: function (data) {
          jQuery(".view-display-id-mar_by_department")
            .empty()
            .html(data.responseText);
        },
      });

      jQuery.ajax({
        type: "GET",
        url: base_url + "/custom/ajax/rs_department",
        data: post_data ? post_data : "department=" + dept,
        dataType: "html",
        async: true,
        complete: function (data) {
          jQuery(".view-display-id-rs_by_department")
            .empty()
            .html(data.responseText);
        },
      });
    });

    var qparam = iitm_app.getQueryParams();
    if (qparam && qparam.department == undefined) {
      var $options = jQuery(".rs_dropdown").find("option");
      var random = Math.floor(Math.random() * $options.length);

      jQuery(".rs_dropdown").val($options.eq(random).val());

      jQuery(".rs_btn_show").trigger("click");
    }
  },
  updateQueryParam: function () {
    var uri = window.location.href.toString();
    if (uri.indexOf("?") > 0) {
      var clean_uri = uri.substring(0, uri.indexOf("?"));
      window.history.propertyIsEnumerable({}, document.title, clean_uri);
    }
  },
  getQueryParams: function () {
    var vars = [],
      hash;
    var hashes = window.location.href
      .slice(window.location.href.indexOf("?") + 1)
      .split("&");

    for (var i = 0; i < hashes.length; i++) {
      hash = hashes[i].split("=");
      vars[hash[0]] = hash[1];
    }
    return vars;
  },
};

